// Product fetch functions
export const fetchAllProducts = async () => {
  const { data, error } = await supabase
    .from('products')
    .select(`
      *,
      product_images (*)
    `)
    .order('created_at', { ascending: false });

  if (error) {
    console.error('Error fetching products:', error);
    throw error;
  }

  return data || [];
};

export const fetchProductById = async (productId: string) => {
  const { data, error } = await supabase
    .from('products')
    .select(`
      *,
      product_images (*)
    `)
    .eq('id', productId)
    .single();

  if (error) {
    console.error(`Error fetching product with ID ${productId}:`, error);
    throw error;
  }

  return data;
};

// Product update functions
export const updateProduct = async (productId: string, updates: any) => {
  const { data, error } = await supabase
    .from('products')
    .update(updates)
    .eq('id', productId)
    .select(`
      *,
      product_images (*)
    `);

  if (error) {
    console.error('Error updating product:', error);
    throw error;
  }

  return data;
};

export const updateProductName = async (productId: string, newName: string) => {
  console.log(`API call: Updating product ${productId} name to "${newName}"`);
  
  // Validate input first
  if (!newName || newName.trim() === '') {
    const validationError = new Error('Product name cannot be empty');
    console.error(validationError);
    throw validationError;
  }
  
  try {
    // Update the product name directly - simplified approach
    const { data, error } = await supabase
      .from('products')
      .update({ name: newName })
      .eq('id', productId)
      .select();
    
    if (error) {
      console.error('Error updating product name:', error);
      throw error;
    }
    
    if (!data || data.length === 0) {
      const updateError = new Error(`Product update failed for ID ${productId}`);
      console.error(updateError);
      throw updateError;
    }
    
    console.log('Basic product update successful, now fetching with images:', data[0]);
    
    // Fetch the complete product with images in a separate query
    const { data: completeProduct, error: fetchError } = await supabase
      .from('products')
      .select(`
        *,
        product_images (*)
      `)
      .eq('id', productId)
      .single();
    
    if (fetchError) {
      console.error('Error fetching complete product data:', fetchError);
      // Return the basic product data if we can't fetch the complete data
      return data[0];
    }
    
    console.log('Product update complete with images:', completeProduct);
    return completeProduct;
    
  } catch (error) {
    console.error('Error in updateProductName:', error);
    throw error;
  }
};

// Real-time subscription function
export const subscribeToProductChanges = (callback: (payload: any) => void) => {
  // Create a channel for listening to product changes
  const productChannel = supabase
    .channel('product-changes')
    .on(
      'postgres_changes',
      {
        event: '*',
        schema: 'public',
        table: 'products'
      },
      (payload) => {
        callback(payload);
      }
    )
    .subscribe();

  // Create a channel for listening to product image changes
  const imagesChannel = supabase
    .channel('product-images-changes')
    .on(
      'postgres_changes',
      {
        event: '*',
        schema: 'public',
        table: 'product_images'
      },
      (payload) => {
        // When a product image changes, we should trigger a refresh of the products
        callback(payload);
      }
    )
    .subscribe();

  // Return a cleanup function that properly removes both channels
  return () => {
    supabase.removeChannel(productChannel);
    supabase.removeChannel(imagesChannel);
  };
};
