// Function to fetch all products from Supabase
export const fetchAllProducts = async () => {
  const { data, error } = await supabase
    .from('products')
    .select(`
      *,
      product_images(*)
    `)
    .order('created_at', { ascending: false });
  
  if (error) {
    console.error("Error fetching products:", error);
    throw error;
  }
  
  return data;
};

// Function to fetch a product by ID
export const fetchProductById = async (productId: string) => {
  const { data, error } = await supabase
    .from('products')
    .select('*, product_images(*)')
    .eq('id', productId)
    .single();
    
  if (error) {
    console.error("Error fetching product:", error);
    throw error;
  }
  
  return data;
};

// Function to update a product
export const updateProduct = async (productId: string, updates: Partial<any>) => {
  console.log("Updating product with ID:", productId, "Updates:", updates);
  
  try {
    // Get the current product data from the database first
    const { data: currentProduct, error: fetchError } = await supabase
      .from('products')
      .select('*')
      .eq('id', productId)
      .single();
      
    if (fetchError) {
      console.error("Error fetching current product data:", fetchError);
      throw fetchError;
    }
    
    console.log("Current product data:", currentProduct);
    
    // Compare if there are actual changes
    let hasChanges = false;
    
    Object.keys(updates).forEach(key => {
      if (key !== 'updated_at' && JSON.stringify(updates[key]) !== JSON.stringify(currentProduct[key])) {
        hasChanges = true;
        console.log(`Field ${key} has changed. Old:`, currentProduct[key], "New:", updates[key]);
      }
    });
    
    if (!hasChanges) {
      console.warn("No actual changes detected in the data being updated");
      // Return a custom response to indicate no changes
      return { noChanges: true };
    }
    
    // Add timestamp to the updates to ensure the change is detected
    const updatesWithTimestamp = {
      ...updates,
      updated_at: new Date().toISOString()
    };
    
    // First log the complete update payload to be sent
    console.log("Full update payload:", updatesWithTimestamp);
    
    const { data, error } = await supabase
      .from('products')
      .update(updatesWithTimestamp)
      .eq('id', productId)
      .select();
      
    if (error) {
      console.error("Error updating product:", error);
      throw error;
    }
    
    console.log("Product updated successfully:", data);
    return data;
  } catch (error) {
    console.error("Exception during product update:", error);
    throw error;
  }
};

// Function to subscribe to products changes - updated to use the channel API
export const subscribeToProductChanges = (callback: () => void) => {
  console.log("Setting up real-time subscription for products table");
  
  // Use the channel API for subscriptions (the legacy .from().on() method is deprecated)
  const channel = supabase
    .channel('product-changes')
    .on(
      'postgres_changes',
      { 
        event: '*', 
        schema: 'public', 
        table: 'products' 
      },
      (payload) => {
        console.log('Real-time product update received:', payload);
        callback();
      }
    )
    .subscribe((status) => {
      console.log('Realtime subscription status:', status);
    });
    
  return channel;
};
